    app.models.predict({id:Clarifai.FACE_DETECT_MODEL, version:''},
    this.state.input)
     .then(//console.log(
      function(response) {

      },
      function(err) {
        // there was an error
      }
    );
  }

  render() {
    return (
      <div className="App">

        <Navigation />
        <Logo />
        <Rank />
        <ImageLinkForm 
          onChangeInput={this.onChangeInput} 
          onSubmit={this.onSubmit} 
          />
        <Particles className='particles'
      params={particlesOptions} />
        <FaceRecognition imageUrl = {this.state.imageUrl}/>
      </div>
    );
  }


}

export default App;
